4 源码解析
    private void linkFirst(E e) {
           //新增节点的next设置为f
        final Node f = first;
        final Node newNode = new Node<>(null, e, f);
        // fisrt指向新节点
        first = newNode;
        // 如果原f为空，说明原列表是空，则first和last都指向newNode
        // 否则fisrt.
        if (f == null)
            last = newNode;
        else
            f.prev = newNode;
        size++;
        modCount++;
    }


    void linkLast(E e) {
        // 新节点的prev设为last，next为null
        final Node l = last;
        final Node newNode = new Node<>(l, e, null);
        // last指向新节点
        last = newNode;
        // 如果l 即新last为null，说明原列表为空，则first和last都指向newNode
        // 否则新增为last的next
        if (l == null)
            first = newNode;
        else
            l.next = newNode;
        size++;
        modCount++;
    }

    // 使用二分法快速加快速度定位到需要的节点
    // 为何不使用多次二分法？
    Node node(int index) {
        if (index > 1)) {
            Node x = first;
            for (int i = 0; i  x = last;
            for (int i = size - 1; i > index; i--)
                x = x.prev;
            return x;
        }
    }

